const char html[] = R"=====(
<html><head><style>p{text-align: center; font-size: 50px; margin-top: 0px;}.middle{text-align: center;}.timercol1{text-align: center; background-color: #4CAF50; border: none; color: white; padding: 15px 32px; text-align: center; text-decoration: none; display: inline-block; font-size: 60px; font-family: 'arial'; margin: 4px 2px;}.button1{text-align: center; background-color: #4CAF50; border: none; color: white; padding: 15px 32px; text-align: center; text-decoration: none; display: inline-block; font-size: 16px; margin: 4px 2px; cursor: pointer;}.timercol2{text-align: center; background-color: #d42222; border: none; color: white; padding: 15px 32px; text-align: center; text-decoration: none; display: inline-block; font-size: 60px; font-family: 'arial'; margin: 4px 2px;}.button2{text-align: center; background-color: #d42222; border: none; color: white; padding: 15px 32px; text-align: center; text-decoration: none; display: inline-block; font-size: 16px; margin: 4px 2px; cursor: pointer;}</style></head><body><div class="middle"><p>Timer 1:</p><p class="timercol1" id='timer1'></p><p></p>Add Time: &nbsp;&nbsp;&nbsp;<span id="browserTimeValue1">0H 0M 0S</span><br><button class="button1" onclick="addTime(1, 0, 0, 1)">+1H</button><button class="button1" onclick="addTime(-1, 0, 0, 1)">-1H</button><button class="button1" onclick="addTime(0, 1, 0, 1)">+1M</button><button class="button1" onclick="addTime(0, -1, 0, 1)">-1M</button><button class="button1" onclick="addTime(0, 0, 1, 1)">+1S</button><button class="button1" onclick="addTime(0, 0, -1, 1)">-1S</button><br><br><button class="button1" onclick="setTimer(1)" id="sec_button">Set Timer 1</button></div><br><br><div class="middle"><p>Timer 2:</p><p class="timercol2" id='timer2'></p><p></p>Add Time: &nbsp;&nbsp;&nbsp;<span id="browserTimeValue2">0H 0M 0S</span><br><button class="button2" onclick="addTime(1, 0, 0, 2)">+1H</button><button class="button2" onclick="addTime(-1, 0, 0, 2)">-1H</button><button class="button2" onclick="addTime(0, 1, 0, 2)">+1M</button><button class="button2" onclick="addTime(0, -1, 0, 2)">-1M</button><button class="button2" onclick="addTime(0, 0, 1, 2)">+1S</button><button class="button2" onclick="addTime(0, 0, -1, 2)">-1S</button><br><br><button class="button2" onclick="setTimer(2)" id="sec_button">Set Timer 2</button></div><script>var httpRequest;var hour=0, min=0, sec=0;if (window.XMLHttpRequest){httpRequest=new XMLHttpRequest()}else if (window.ActiveXObject){httpRequest=new ActiveXObject()}function addTime(h, m, s, timerIndex){hour +=h; min +=m; sec +=s; if(hour < 0){hour=0;}if(min < 0){min=0;}else if(min > 60){min=60;}if(sec < 0){sec=0;}else if(sec > 60){sec=60;}document.getElementById("browserTimeValue"+timerIndex).innerHTML=hour + "H " + min + "M " + sec + "S";}function res_handle(){var data=httpRequest.responseText; if(data !=""){list=data.split('#'); document.getElementById('timer1').innerHTML=list[0] + 'h ' + list[1] + 'm ' + list[2] + 's '; document.getElementById('timer2').innerHTML=list[3] + 'h ' + list[4] + 'm ' + list[5] + 's ';}}function GetData(){httpRequest.open("GET", "/data", true); httpRequest.onreadystatechange=res_handle; httpRequest.send();}function setTimer(timerIndex){document.getElementById("browserTimeValue"+timerIndex).innerHTML="0H 0M 0S"; httpRequest.open("GET", `/sendData?timer=${timerIndex}&hours=${hour}&mins=${min}&seconds=${sec}`, true); httpRequest.send(); hour=0; min=0; sec=0;}setInterval(GetData, 400);</script></body> </html>)=====";
